// 1. Get the input data (the rows from your Excel file)
const inputData = $items().map(item => item.json);

// 2. Group the data by 'User Name' and then by 'Role Name'
const groupedByUser = {};

for (const row of inputData) {
  const userName = row['User Name'];
  const roleName = row['Role Name'];
  const tCode = row['T.Code'];

  // If the user doesn't exist yet, create an entry for them
  if (!groupedByUser[userName]) {
    groupedByUser[userName] = {
      roles: {}
    };
  }

  // If the role for this user doesn't exist, create it
  if (!groupedByUser[userName].roles[roleName]) {
    groupedByUser[userName].roles[roleName] = [];
  }

  // Add the T-Code to the correct role
  groupedByUser[userName].roles[roleName].push(tCode);
}

// 3. Format the grouped data into the final desired structure
const finalOutput = [];

for (const userName in groupedByUser) {
  const user = groupedByUser[userName];
  for (const roleName in user.roles) {
    const tCodes = user.roles[roleName];

    // Join T-Codes, remove duplicates, sort them, and make them uppercase
    const allTCodesString = [...new Set(tCodes)]
      .filter(tc => typeof tc === 'string' && tc.trim() !== '')
      .map(tc => tc.trim().toUpperCase())
      .sort()
      .join(', ');

    finalOutput.push({
      'User Name': userName,
      'Role Name': roleName,
      'All_T-codes': allTCodesString
    });
  }
}

// 4. Return the final result as a single n8n item
// This makes it easy for the next node to use.
return [{ json: { data: finalOutput } }];

// 1ï¸âƒ£ Get all rows from the Excel input
const inputData = $items().map(item => item.json);

// 2ï¸âƒ£ Prepare the output array
const finalOutput = [];

for (const row of inputData) {
  const rowNumber = row['row_number'];
  const sNo = row['S.No'];
  const ruleSet = row['Rule Set'];
  const ap1 = row['Access Point 1 T-codes'];
  const ap2 = row['Access Point 2 T-codes'];

  // ðŸ§¹ Clean, uppercase, deduplicate, and sort Access Point 1
  const ap1Codes = (typeof ap1 === 'string' && ap1.trim() !== '')
    ? [...new Set(ap1.split(',').map(tc => tc.trim().toUpperCase()))].sort().join(', ')
    : '';

  // ðŸ§¹ Clean, uppercase, deduplicate, and sort Access Point 2
  const ap2Codes = (typeof ap2 === 'string' && ap2.trim() !== '')
    ? [...new Set(ap2.split(',').map(tc => tc.trim().toUpperCase()))].sort().join(', ')
    : '';

  // âœ… Push cleaned data as a structured row
  finalOutput.push({
    'row_number': rowNumber,
    'S.No': sNo,
    'Rule Set': ruleSet,
    'Access Point 1 T-codes': ap1Codes,
    'Access Point 2 T-codes': ap2Codes
  });
}

// 3ï¸âƒ£ Return each row as a separate n8n item
return [{ json: { data: finalOutput } }];

// âœ… Step 1: Extract and safely parse input
const inputItem = $input.first().json;

// Extract raw rule and user data
let rulesRaw = inputItem.rule || inputItem.rules; // handle both 'rule' or 'rules'
let usersRaw = inputItem.users;

// ##########################################################################
// ðŸ§© Parse RULES safely
// ##########################################################################
let rules = [];
try {
  const parsedRules = typeof rulesRaw === 'string' ? JSON.parse(rulesRaw) : rulesRaw;

  if (Array.isArray(parsedRules)) {
    if (parsedRules[0]?.data && Array.isArray(parsedRules[0].data)) {
      rules = parsedRules[0].data;
    } else if (parsedRules[0]?.["Rule Set"]) {
      rules = parsedRules;
    } else if (Array.isArray(parsedRules[0])) {
      rules = parsedRules[0];
    }
  }
} catch (err) {
  return [{ json: { error: "âŒ Failed to parse rules JSON", details: err.message, raw: rulesRaw } }];
}

// ##########################################################################
// ðŸ§© Parse USERS safely
// ##########################################################################
let users = [];
try {
  const parsedUsers = typeof usersRaw === 'string' ? JSON.parse(usersRaw) : usersRaw;

  if (Array.isArray(parsedUsers)) {
    if (parsedUsers[0]?.data && Array.isArray(parsedUsers[0].data)) {
      users = parsedUsers[0].data;
    } else if (parsedUsers[0]?.["User Name"]) {
      users = parsedUsers;
    }
  }
} catch (err) {
  return [{ json: { error: "âŒ Failed to parse users JSON", details: err.message, raw: usersRaw } }];
}

// ##########################################################################
// ðŸ§¾ Validation
// ##########################################################################
if (!Array.isArray(rules) || rules.length === 0) {
  return [{ json: { error: "âŒ No valid rules found", received: rulesRaw } }];
}
if (!Array.isArray(users) || users.length === 0) {
  return [{ json: { error: "âŒ No valid users found", received: usersRaw } }];
}

// ##########################################################################
// ðŸ‘¥ Group users by user name (multi-role handling)
// ##########################################################################
const usersGrouped = {};

for (const user of users) {
  const userName = user["User Name"] || "Unnamed User";
  const roleName = user["Role Name"] || "Unnamed Role";
  const tcodeString = user["All_T-codes"] || "";

  if (!tcodeString.trim()) continue;

  const roleTcodes = tcodeString
    .split(",")
    .map(tc => tc.trim().toUpperCase())
    .filter(tc => tc);

  if (!usersGrouped[userName]) {
    usersGrouped[userName] = { roles: [] };
  }

  usersGrouped[userName].roles.push({ roleName, tcodes: roleTcodes });
}

const results = [];

// ##########################################################################
// ðŸŸ¥ INTRA-ROLE (within same role)
// ##########################################################################
for (const userName in usersGrouped) {
  for (const role of usersGrouped[userName].roles) {
    for (const rule of rules) {
      const ap1 = (rule["Access Point 1 T-codes"] || "")
        .split(",")
        .map(tc => tc.trim().toUpperCase())
        .filter(tc => tc);
      const ap2 = (rule["Access Point 2 T-codes"] || "")
        .split(",")
        .map(tc => tc.trim().toUpperCase())
        .filter(tc => tc);

      const hasAP1 = ap1.some(tc => role.tcodes.includes(tc));
      const hasAP2 = ap2.some(tc => role.tcodes.includes(tc));

      if (hasAP1 && hasAP2) {
        results.push({
          json: {
            "User Name": userName,
            "Role Name": role.roleName,
            "Conflict Type": "ðŸŸ¥ Intra-Role",
            "Rule No": rule["S.No"] || "",
            "Rule Description": rule["Rule Set"] || "",
            "Access Point 1": ap1.join(", "),
            "Access Point 2": ap2.join(", "),
            "Matched T-codes": [...ap1.filter(tc => role.tcodes.includes(tc)), ...ap2.filter(tc => role.tcodes.includes(tc))].join(", ")
          }
        });
      }
    }
  }
}

// ##########################################################################
// âœ… Return results or summary
// ##########################################################################
if (results.length === 0) {
  return [{
    json: {
      message: "âœ… No Intra-Role SoD violations found",
      stats: {
        usersProcessed: Object.keys(usersGrouped).length,
        rolesProcessed: users.length,
        rulesProcessed: rules.length
      }
    }
  }];
}

return results;

// === Get and Parse Input ===
const inputItem = $input.first().json;

let rules = inputItem.rule;
let users = inputItem.users;

if (typeof rules === 'string') rules = JSON.parse(rules);
if (typeof users === 'string') users = JSON.parse(users);

rules = Array.isArray(rules) && rules[0]?.data ? rules[0].data : rules;
users = Array.isArray(users) && users[0]?.data ? users[0].data : users;

if (!Array.isArray(rules) || !Array.isArray(users)) {
    return [{ json: { message: "Invalid input format" } }];
}

// === Normalize helper ===
function normalizeTcodes(str) {
    if (!str) return [];
    return [...new Set(
        str
            .toString()
            .replace(/[^\x00-\x7F]/g, '') // remove hidden chars
            .split(/[,;\/\s]+/)
            .map(tc => tc.trim().toUpperCase())
            .filter(tc => tc)
    )];
}

// === Step 1: Group user roles and tcodes ===
const usersGrouped = {};
for (const u of users) {
    const user = (u["User Name"] || "").trim();
    const role = (u["Role Name"] || "").trim();
    if (!user || !role) continue;

    const tcodes = normalizeTcodes(u["All_T-codes"]);
    if (!tcodes.length) continue;

    if (!usersGrouped[user]) usersGrouped[user] = {};
    usersGrouped[user][role] = tcodes;
}

// === Step 2: Detect all Inter-Role conflicts per rule ===
const results = [];

for (const user in usersGrouped) {
    const roleNames = Object.keys(usersGrouped[user]);
    if (roleNames.length < 2) continue;

    for (const rule of rules) {
        const ruleNo = rule["S.No"] || "";
        const ruleDesc = rule["Rule Set"] || rule["Rule Description"] || "";
        const ap1 = normalizeTcodes(rule["Access Point 1 T-codes"]);
        const ap2 = normalizeTcodes(rule["Access Point 2 T-codes"]);
        if (!ap1.length || !ap2.length) continue;

        // find roles matching each access point
        const rolesAP1 = {};
        const rolesAP2 = {};
        for (const r of roleNames) {
            const tcodes = usersGrouped[user][r];
            const match1 = ap1.filter(tc => tcodes.includes(tc));
            const match2 = ap2.filter(tc => tcodes.includes(tc));
            if (match1.length) rolesAP1[r] = match1;
            if (match2.length) rolesAP2[r] = match2;
        }

        // now compare every role in AP1 vs AP2
        for (const r1 in rolesAP1) {
            for (const r2 in rolesAP2) {
                if (r1 === r2) continue; // inter-role only
                for (const tc1 of rolesAP1[r1]) {
                    for (const tc2 of rolesAP2[r2]) {
                        results.push({
                            json: {
                                "User": user,
                                "Rule No": ruleNo,
                                "Rule Description": ruleDesc,
                                "Roles Involved": `${r1} â†” ${r2}`,
                                "Matched T-codes": `${tc1} (${r1}), ${tc2} (${r2})`,
                                "Conflict Type": "Inter-Role"
                            }
                        });
                    }
                }
            }
        }
    }
}

return results.length ? results : [{ json: { message: "No SoD conflicts found" } }];

